   SET PNAME = #{pname}, UNITPRICE = #{unitPrice}, DESCRIPTION = #{description},
       CATEGORY = #{category}, MANUFACTURER = #{manufacturer}, UNITSINSTOCK = #{unitsInStock},
       CONDITION = #{condition}, FILENAME = #{filename}
 WHERE PRODUCTID = #{productId}

